import React from 'react';
import Axios from 'axios';

class PokemonElements extends React.Component{

    constructor(props) {
        super(props);
        this.state = {
            isLoading: false,
            url: 'https://pokeapi.co/api/v2/pokemon/?limit=151',
            spriteUrl: '',
            name:'',
            types: [],
            hp: '',
            attack:'',
            defense:'',
            specialAttack:'',
            specialDefense:'',
            speed:'',
            height:'',
            weight:'',
        };
      }

      async componentDidMount(){
          const { pName } = this.props.match.params;
          console.log(pName);
          const pUrl = `https://pokeapi.co/api/v2/pokemon/${pName}`;
          const pResponse = await Axios.get(pUrl);
          const types = pResponse.data.types.map(type => {
            type.type.name,
            type.type.url
            });


          this.setState({
            name: pResponse.data.name,
            spriteUrl: pResponse.data.sprites.front_default,
            speed: pResponse.data.stats[0].base_stat,
            specialDefense: pResponse.data.stats[1].base_stat,
            specialAttack: pResponse.data.stats[2].base_stat,
            defense: pResponse.data.stats[3].base_stat,
            attack: pResponse.data.stats[4].base_stat,
            hp: pResponse.data.stats[5].base_stat,
            height: pResponse.data.height,
            weight: pResponse.data.weight,
            types,
            move1: pResponse.data.moves[0].name,
            move2: pResponse.data.moves[1].name,
            move3: pResponse.data.moves[2].name,

          })
      }

    render(){

        const {
            name,
            spriteUrl,
            speed,
            specialAttack,
            specialDefense,
            defense,
            attack,
            hp,
            types, 
            move1,
            move2,
            move3,
            height,
            weight
        } = this.state;
        console.log(name,
            spriteUrl,
            speed,
            specialAttack,
            specialDefense,
            defense,
            attack,
            hp,
            types, 
            move1,
            move2,
            move3,
            height,
            weight)

        return(
            <div className="col-md-6 col-sm-6 mb-2" style={{paddingTop: 6 + 'em'}}>
                <div className="card">
                    <img src={this.state.spriteUrl} className="card-img-top" alt={this.state.name} />
                </div>
                <div className="card">
                    <h1>{pName}</h1>

                </div>
            </div> 
        )
    }
}
export default PokemonElements;
