import React from 'react';
import Axios from 'axios';

class PokemonElements extends React.Component{

    constructor(props) {
        super(props);
        this.state = {
            pName: '',
            isLoading: false,
            url: 'https://pokeapi.co/api/v2/pokemon/?limit=151',
            spriteUrl: '',
            name:'',
            types: [],
            hp: '',
            attack:'',
            defense:'',
            specialAttack:'',
            specialDefense:'',
            speed:'',
            height:'',
            weight:'',
        };
      }

      async componentDidMount(){
          const { pName } = this.props.match.params;
          console.log(pName);
          const pUrl = `https://pokeapi.co/api/v2/pokemon/${pName}`;
          const pResponse = await Axios.get(pUrl);
          switch (pResponse.data.types.length()){
              case 1:
                    const Type = pResponse.data.types[0].type;
                    const urlType = `/picture/types/${Type}.png`;
                      break;
              case 2:
                    const Type2 = pResponse.data.types[0].type;
                    const Type3 = pResponse.data.types[1].type;
                    const urlType2 = `/picture/types/${Type2}.png`;
                    const urlType3 = `/picture/types/${Type3}.png`;
          }


          this.setState({
            name: pResponse.data.name,
            spriteUrl: pResponse.data.sprites.front_default,
            speed: pResponse.data.stats[0].base_stat,
            specialDefense: pResponse.data.stats[1].base_stat,
            specialAttack: pResponse.data.stats[2].base_stat,
            defense: pResponse.data.stats[3].base_stat,
            attack: pResponse.data.stats[4].base_stat,
            hp: pResponse.data.stats[5].base_stat,
            height: pResponse.data.height,
            weight: pResponse.data.weight,
            move1: pResponse.data.moves[0].move.name,
            move2: pResponse.data.moves[1].move.name,
            move3: pResponse.data.moves[2].move.name,
            urlTypes,

          })
      }

    render(){

        const {
            name,
            spriteUrl,
            speed,
            specialAttack,
            specialDefense,
            defense,
            attack,
            hp,
            types, 
            move1,
            move2,
            move3,
            height,
            weight,
            urlTypes,
        } = this.state;
        console.log(name,
            spriteUrl,
            speed,
            specialAttack,
            specialDefense,
            defense,
            attack,
            hp,
            types, 
            move1,
            move2,
            move3,
            height,
            weight,
            urlTypes
            )
            console.log(this.state.urlTypes);
        return(
            <div className="d-flex flex-row justify-content-around" style={{paddingTop: 6 + 'em'}}>
                <div className="card bg-light col-md-5 col-sm-6">
                    <img src={this.state.spriteUrl} className="card-img-top" alt={this.state.name} />
                </div>
                <div className="card bg-light col-md-5 col-sm-6">
                    <br></br>
                    <h1>{this.state.name}</h1>
                    Height: {this.state.height}dm / Weight: {this.state.weight}
                    <br></br>
                    <br></br>
                    <h4>Stats</h4>
                    <p className="card-text">
                        Speed: {this.state.speed}<br></br>
                        Health Point: {this.state.hp}<br></br>
                        Attack: {this.state.attack}<br></br>
                        Defense: {this.state.defense}<br></br>
                        Special Attack: {this.state.specialAttack}<br></br>
                        Special Defense: {this.state.specialDefense}<br></br>
                    </p>
                    <h4>Skills</h4>
                    <p className="card-text raw">
                        {this.state.move1} / {this.state.move2} / {this.state.move3} 
                    </p>

                    <img src={this.state.urlTypes} className="card-img-bottom" alt={this.state.name} />
                </div>
            </div> 
        )
    }
}
export default PokemonElements;
