import React from 'react';
//import './Item.css';

class Item extends React.Component {

    constructor(props){
        super(props);
        this.state = { 
            isLoading: false,
            pokemon: [],
            sprite:'',
            abilities: [],
            namePokemon:'',


        }; 
    }

    async componentDidMount() {

        this.setState({isLoading: true});
        const { namePokemon } = this.props.namePokemon;
        try{
            const response = await fetch(`https://pokeapi.co/api/v2/pokemon/${namePokemon}`);
            const data = await response.json();

            this.setState({ pokemon: data,
                            isLoading: false,
                            sprite: data.sprites.front_default,
                            fabilities: data.abilities[0].ability.name,
                            sabilities: data.abilities[1].ability.name,
            })

        }catch (err){
            console.log(err.msg);
            this.setState({isLoading: false});
            throw err;
        }

      }

    render(){

        const { isLoading, pokemon, sprite, fabilities, sabilities } = this.state;
        console.log(fabilities);
        return (
            <li>
                <h2>{pokemon.name}</h2>
                <img src={sprite} alt="pokemon"></img>
                <p>{pokemon.height}</p>
                <p>{pokemon.weight}</p>
                <p>{fabilities}</p>
                <p>{sabilities}</p>
            </li>
        )
    }
}

export default Item;
