        const spriteUrl =`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${pIndex}`;
        console.log({spriteUrl});

        this.setState({name, pIndex, spriteUrl})
    }

    render(){
        return (
            <div className="col-md-4 col-sm-6 mb-5">
                <div className="card">
                    <img src={spriteUrl} className="card-img-top" alt="..." />
                    <div className="card-body">
                    <h5 className="card-title center">{this.state.name}</h5>
                    </div>
                </div> 
            </div>
        )
    };
}

export default Item;
